mapper.select:
SELECT 

FROM ee_emp_plan a

WHERE a.id = #{id}

SELECT 

FROM ee_emp_plan a


	a.del_flag = #{DEL_FLAG_NORMAL}

	and a.app_id = #{appId}




		ORDER BY ${page.orderBy}


		ORDER BY a.line_no

SELECT 

FROM ee_emp_plan a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ee_emp_plan SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
