mapper.select:
SELECT o.name AS organizeName,

FROM ee_sale_emp a
left join sys_office o
on a.organize = o.id
where a.create_by = #{createBy.id}
	and a.year = #{year}

SELECT 

FROM ee_sale_emp a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ee_sale_emp a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ee_sale_emp SET 	
	proc_ins_id = #{procInsId},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	year = #{year}
WHERE id = #{id}

UPDATE ee_sale_emp SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
