mapper.select:
SELECT 

FROM pm_purchase_ord a

WHERE a.id = #{id}

SELECT 

FROM pm_purchase_ord a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pm_purchase_ord a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 
	,
	b.supplier_name AS "supplierName"
FROM pm_purchase_ord a
LEFT JOIN cm_supplier_info b ON b.supplier_id = a.supplier_id
WHERE a.purchase_no = #{purchaseNo}
AND a.del_flag = #{DEL_FLAG_NORMAL}
AND b.del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
UPDATE pm_purchase_ord SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
