mapper.select:
SELECT 

FROM rm_quotation_dtl a

WHERE a.id = #{id}

SELECT 

FROM rm_quotation_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM rm_quotation_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE rm_quotation_dtl SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM rm_quotation_dtl a


    a.del_flag = #{DEL_FLAG_NORMAL}

    AND a.id = #{id}


    AND a.quotation_no = #{quotationNo}


    AND a.line_no = #{lineNo}


    AND a.repair_no = #{repairNo}

SELECT , c.update_date AS "repairDate", c.status_remarks AS "statusRemarks", CONCAT(d.material_name, " ", d.model) AS "materialName", e.production_date AS "productionDate" FROM rm_quotation_dtl a INNER JOIN rm_quotation b on b.quotation_no = a.quotation_no LEFT JOIN rm_repair_info c on c.repair_no = a.repair_no LEFT JOIN sm_mat_info d on d.material_no = a.material_no LEFT JOIN sm_sn_info e on e.sn_no = a.sn_no
b.quotation_type = '2' AND a.item_type = '1' AND a.if_purchase = '0' AND a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY a.repair_no DESC

mapper.update:
UPDATE rm_quotation_dtl SET 
	if_purchase = #{ifPurchase}


    quotation_no = #{quotationNo}


    AND line_no = #{lineNo}


    AND repair_no = #{repairNo}
