mapper.select:
SELECT 

FROM sm_mat_info a

WHERE a.id = #{id}

SELECT 

FROM sm_mat_info a
WHERE a.material_no = #{materialNo}

AND a.material_type = #{materialType}

AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT 

FROM sm_mat_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sm_mat_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sm_mat_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
