mapper.select:
SELECT
,b.price_system as priceSystem ,(select label from sys_dict where type='DM0020' and value=b.price_system) as priceSystemName ,(select label from sys_dict where type='DM0049' and value=a.region) as regionName ,(select label from sys_dict where type='DM0002' and value=a.industry) as industryName FROM ps_apply_price_dtl a
WHERE a.id = #{id}

SELECT 
	,a.price_system AS priceSystem,concat(m.material_Name," ",m.model) AS materialName
	,(select label from sys_dict where type='DM0020' and value=a.price_system) as priceSystemName
	,(select label from sys_dict where type='DM0049' and value=a.region) as regionName
	,(select label from sys_dict where type='DM0002' and value=a.industry) as industryName
	,cu.name as "createBy.name"
FROM ps_price_info a
LEFT JOIN sm_mat_info m ON a.material_no = m.material_no and m.del_flag = 0
LEFT JOIN cm_agreement c ON c.id = a.agreement_id and c.del_flag = 0
LEFT JOIN cm_customer_info cus ON cus.customer_id = a.agreement_paties_id
LEFT JOIN sys_user cu ON cu.id = a.create_by

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND m.material_Name like CONCAT('%', #{materialName}, '%')


		AND a.price_System = #{priceSystem}


		AND a.agreement_Paties_Id = #{agreementPatiesId}


		AND a.create_by = #{createBy.id}


	    AND a.create_by in ${sqlMap.dataScope}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

	,b.price_system AS priceSystem,CONCAT(m.material_Name,' ',m.model) AS materialName
	,(select label from sys_dict where type='DM0020' and value=b.price_system) as priceSystemName
	,(select label from sys_dict where type='DM0049' and value=a.region) as regionName
	,(select label from sys_dict where type='DM0002' and value=a.industry) as industryName
FROM ps_apply_price_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.app_id = #{appId}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM ps_apply_price_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE ps_apply_price_dtl SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE app_id = #{appId}
