SELECT , b.invoice_title AS "invoiceTitle", b.workflow_status AS "workflowStatus" FROM im_invoice_dtl a INNER JOIN im_invoice b ON b.id = a.app_id INNER JOIN rm_quotation c ON c.quotation_no = a.order_no
a.del_flag = '0' AND b.del_flag = '0' AND c.del_flag = '0' AND c.id = #{quotaId}
ORDER BY b.create_date ASC, a.app_id ASC, a.line_no2 ASC
