mapper.select:
SELECT 

FROM rm_quotation_dtl a

WHERE a.id = #{id}

SELECT 

FROM rm_quotation_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.quotation_no = #{quotationNo}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM rm_quotation_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.delete:
DELETE FROM rm_quotation_dtl


		WHERE id = #{id}


		WHERE quotation_no = #{quotationNo}

mapper.select:
SELECT , IF(c.material_type='6', CONCAT(c.material_name, " ", c.model), c.material_name) AS "materialName", c.material_name AS "materialNameForExport", c.model AS "modelForExport", c.if_sn AS "ifSn", d.production_date AS "newProductionDate", e.production_date AS "productionDate" FROM rm_quotation_dtl a INNER JOIN rm_quotation b ON b.quotation_no = a.quotation_no LEFT JOIN sm_mat_info c ON c.material_no = a.material_no LEFT JOIN sm_sn_info d ON d.sn_no = a.new_sn_no LEFT JOIN sm_sn_info e ON e.sn_no = a.sn_no
a.del_flag = '0' AND b.del_flag = '0' AND b.id = #{id}
ORDER BY a.line_no ASC
