mapper.select:
SELECT 

FROM sm_warehouse_info a

WHERE a.id = #{id}

SELECT 

FROM sm_warehouse_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sm_warehouse_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sm_warehouse_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT , b.id AS "quotaDtlId" FROM sm_warehouse_info a INNER JOIN rm_quotation_dtl b ON b.material_no = a.material_no and b.warehouse = a.warehouse INNER JOIN rm_quotation c ON c.quotation_no = b.quotation_no
a.del_flag = '0' AND b.del_flag = '0' AND c.del_flag = '0' AND a.in_stock_status = '1' AND trim(IFNULL(a.sn_no, '')) = trim(IFNULL(b.new_sn_no, '')) AND c.id = #{quotaId}
ORDER BY b.line_no ASC
