SELECT 

	, m.model
FROM bo_business_opp_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.business_opp_no = #{businessOppNo}




		ORDER BY ${page.orderBy}


		ORDER BY a.business_opp_no,a.line_no

