mapper.select:
SELECT 

FROM so_order_dtl a
WHERE a.order_id = #{orderId}

	AND a.line_no = #{lineNo}


	AND a.material_no = #{materialNo}


	AND a.package_mertiral_no = #{packageMertiralNo}

SELECT 

FROM so_order_dtl a

WHERE a.id = #{id}

SELECT 

FROM so_order_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.order_id = #{orderId}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM so_order_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE so_order_dtl SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE order_id = #{orderId}
