mapper.select:
SELECT
,m.model AS "model" FROM ta_tender_author_dtl a
WHERE a.id = #{id}

SELECT 

	,m.model AS "model"
FROM ta_tender_author_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.app_id = #{appId}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

	,m.model AS "model"
FROM ta_tender_author_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE ta_tender_author_dtl SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE app_id = #{appId}
