mapper.select:
SELECT 

FROM vr_visit a

WHERE a.id = #{id}

SELECT 

FROM vr_visit a

WHERE a.proc_ins_id = #{procInsId}

SELECT 

FROM vr_visit a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM vr_visit a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
