mapper.select:
SELECT 

FROM goods a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.typeId = b.id

		AND a.typeid = #{typeid}


		AND a.sellerid = #{sellerid}


		AND a.price <= #{price}


		AND a.count <= #{count}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM goods a


	a.del_flag = #{DEL_FLAG_NORMAL}

	and a.typeId = b.id


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE goods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
