mapper.select:
SELECT 

FROM gym a

WHERE a.id = #{id}

SELECT 

FROM gym a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.video_name LIKE 
			'%'||#{videoName}||'%'
			'%'+#{videoName}+'%'
			concat('%',#{videoName},'%')


		AND a.seller_id = #{sellerId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gym a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gym SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
