mapper.select:
SELECT 

FROM demo_classes a

WHERE a.id = #{id}

SELECT
FROM demo_classes a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.classname LIKE '%'||#{classname}||'%' '%'+#{classname}+'%' concat('%',#{classname},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM demo_classes a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
